INSERT INTO `epuz_site_htmlsnippets` VALUES ('9','oplata_page','','2','none','0','0','<section id=\"basket\">\n		<div class=\"container\">\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<h2 class=\"basketh2\">Способы оплаты </h2>\n				</div>				\n			</div>	\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<div class=\"dop\">\n						<p class=\"dostavka\">В настоящий момент доступны следующие способы оплаты товара, заказываемого в интернет-магазине \"Тимберк\":</p>\n\n						<p class=\"dostavka\">• Оплата наличными нашему курьеру при получении товара (только для Киева);</p> \n						<p class=\"dostavka\">• Наложенный платеж через сервис доставки \"Нова Пошта\";</p> \n						<p class=\"dostavka\">• Оплата банковской картой Visa / Mastercard;</p>					\n					</div>				\n				</div>				\n			</div>\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<h2 class=\"basketh2\">График доставки</h2>\n				</div>				\n			</div>	\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<div class=\"dop\">\n						<p class=\"dostavka\">Доставка по Киеву осуществляется на протяжении 1-2 дней c момента поступления заказа по предварительному согласованию с покупателем. </p>\n						<p class=\"dostavka\">График доставки по Киеву: Пн-Пт с 12:00 до 18:00, Суббота и Воскресенье - выходные. </p>\n						<p class=\"dostavka\">Отправка по Украине осуществляется на протяжении 1-3 дней c момента поступления заказа через сервис доставки \"Нова Пошта\".	</p>\n						<p class=\"dostavka\"><b>Связаться с нами:</b> <a href=\"tel:+38 (044) 587 83 18\">+38 (044) 587 83 18</a> , <a href=\"tel:+38 (044) 587 83 18\">+38 (044) 587 83 18</a></p>\n					</div>				\n				</div>				\n			</div>			\n		</div>			\n	</section>','0','1531321893','1532416891','0');
INSERT INTO `epuz_site_htmlsnippets` VALUES ('10','about_page','','2','none','7','0','<section id=\"about\">\n		<div class=\"container\">\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<h2>О нас</h2>\n				</div>				\n			</div>	\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<div class=\"ab_content\">\n						<strong>В интернет-магазине \"AQUA VIP\" </strong>\n						<p><img src=\"assets/templates/img/check.png\" class=\"check\">Только по-настоящему качественные и достойные товары.</p>\n						<p><img src=\"assets/templates/img/check.png\" class=\"check\">Наш магазин  идеальное место для выбора товаров для здоровья, бытовой техники</p>\n						<p><img src=\"assets/templates/img/check.png\" class=\"check\">Наличие собственной курьерской службы позволяет осуществлять быструю доставку.</p>\n						<p><img src=\"assets/templates/img/check.png\" class=\"check\">При заказе по Киеву большинство товаров доставляется на следующий день после заказа</p>\n						<p><img src=\"assets/templates/img/check.png\" class=\"check\">При необходимости мы предоставляем услуги срочной доставки.</p>\n					</div>			\n				</div>			\n			</div>					\n		</div>			\n	</section>\n	\n	<section id=\"reviews\">\n		<div class=\"container\">\n			<div class=\"row\">\n				<div class=\"col-md-12\">\n					<h2>Отзывы</h2>\n				</div>				\n			</div>				\n				[!MultiTV?\n					&tvName=`commentary`\n					&docid=`4`\n					&tplConfig=`commentary`\n					&outerTpl=`<div class=\"row\">[+wrapper+]</div>`					\n					&rowTpl=`commentary_tpl`\n					&display=`all`\n				!]								\n		</div>			\n	</section>	','0','1531321967','1531475549','0');
