INSERT INTO `epuz_site_plugins` VALUES ('11','ManagerManager','<strong>0.6.3</strong> Customize the EVO Manager to offer bespoke admin functions for end users or manipulate the display of document fields in the manager.','0','4','0','\n/**\n * ManagerManager\n *\n * Customize the EVO Manager to offer bespoke admin functions for end users or manipulate the display of document fields in the manager.\n *\n * @category plugin\n * @version 0.6.3\n * @license http://creativecommons.org/licenses/GPL/2.0/ GNU Public License (GPL v2)\n * @internal @properties &remove_deprecated_tv_types_pref=Remove deprecated TV types;list;yes,no;yes &config_chunk=Configuration Chunk;text;mm_rules\n * @internal @events OnDocFormRender,OnDocFormPrerender,OnBeforeDocFormSave,OnDocFormSave,OnDocDuplicate,OnPluginFormRender,OnTVFormRender\n * @internal @modx_category Manager and Admin\n * @internal @installset base\n * @internal @legacy_names Image TV Preview, Show Image TVs\n * @reportissues https://github.com/DivanDesign/MODXEvo.plugin.ManagerManager/\n * @documentation README [+site_url+]assets/plugins/managermanager/readme.html\n * @documentation Official docs http://code.divandesign.biz/modx/managermanager\n * @link        Latest version http://code.divandesign.biz/modx/managermanager\n * @link        Additional tools http://code.divandesign.biz/modx\n * @link        Full changelog http://code.divandesign.biz/modx/managermanager/changelog\n * @author      Inspired by: HideEditor plugin by Timon Reinhard and Gildas; HideManagerFields by Brett @ The Man Can!\n * @author      DivanDesign studio http://www.DivanDesign.biz\n * @author      Nick Crossland http://www.rckt.co.uk\n * @author      Many others\n * @lastupdate  22/01/2018\n */\n\n// Run the main code\ninclude($modx->config[\'base_path\'].\'assets/plugins/managermanager/mm.inc.php\');\n','0','{\"remove_deprecated_tv_types_pref\":[{\"label\":\"Remove deprecated TV types\",\"type\":\"list\",\"value\":\"yes\",\"options\":\"yes,no\",\"default\":\"yes\",\"desc\":\"\"}],\"config_chunk\":[{\"label\":\"Configuration Chunk\",\"type\":\"text\",\"value\":\"mm_rules\",\"default\":\"mm_rules\",\"desc\":\"\"}]}','0','','0','0');
INSERT INTO `epuz_site_plugins` VALUES ('12','Clear Comments','<strong>1.0</strong> Remove comments of documents when you empty MODx Trash Can.','0','0','0','/**\n * Clear Comments Plugin: Remove comments of documents when you empty MODx Trash Can.\n * Version: 1.0\n * Event: \"OnEmptyTrash\"\n * Requirement: Jot Snippet\n * Work on: Evolution\n *\n * Author: AHHP ~ Boplo.ir\n * Date: 21 August 2009\n*/\n\ndefined(\'IN_MANAGER_MODE\') or die();\nif($modx->Event->name == \"OnEmptyTrash\")\n{\n	$where = \'uparent IN(\' .join(\',\' , $ids). \')\';\n	$jot_content = $modx->getFullTableName(\'jot_content\');\n	$jot_fields = $modx->getFullTableName(\'jot_fields\');\n	$jot_subscriptions = $modx->getFullTableName(\'jot_subscriptions\');\n	\n	\n	// If `jot_fields` exists\n	if($modx->db->getRecordCount( $modx->db->query(\"SHOW TABLES LIKE \'$jot_fields\'\") ) == 1)\n	{\n		// `jot_fields` stores fields by comments ID so we need to get comments ID that are removing.\n		$commentsIds = array();\n		$select = $modx->db->select(\"id\", $jot_content, $where);\n		while($commentRow = $modx->db->getRow($select, \'num\'))\n			$commentsIds[] = $commentRow[0];\n		\n		if(count($commentsIds) > 0)\n			$modx->db->delete($jot_fields, \'id IN(\' .join(\",\",$commentsIds). \')\');\n		\n		unset($select, $commentsIds);\n	}\n	\n	$modx->db->delete($jot_content, $where);\n	$modx->db->delete($jot_subscriptions, $where);\n}','0','','1','','0','0');
INSERT INTO `epuz_site_plugins` VALUES ('13','JotAdminNotify','<strong>0.1</strong> На главной странице админки отображается количество неопубликованных коментариев снипета JotX','0','0','0','$output = \"\";\n$e = &$modx->Event;\nif($e->name == \'OnManagerWelcomePrerender\'){\n\n    $table = $modx->getFullTableName(\'jot_content\');\n    $sitecontent = $modx->getFullTableName(\'site_content\');\n    $rs = $modx->db->query(\"SELECT count(jc.uparent) as count, jc.uparent, sc.pagetitle FROM $table jc left join $sitecontent sc on sc.id = uparent where jc.published=0 group by jc.uparent\");\n    while($row=$modx->db->GetRow($rs)){\n        if ($row[\'count\']>0){\n            $id = $row[\'uparent\'];\n            $count = $row[\'count\'];\n            $url = $modx->makeUrl($id);\n            $output .= \"<li><a href=\'$url\' target=\'_blank\'>\".$row[\'pagetitle\'].\": $count</a></li>\";\n        }\n    }\n    \n    if (!empty($output)){\n        $output = \'<div class=\"sectionHeader\" style=\"color:red\">Имеются неопубликованные комментарии</div><div class=\"sectionBody\"><ul>\'.$output.\'</ul></div>\';\n    }\n    $e->output($output);\n}\n','0','','1','','0','0');
