INSERT INTO `epuz_site_plugins` VALUES ('15','ajaxSubmit','<strong>1.0</strong> Ajax sending of any form','0','9','0','if(!isset($post_signal)) $post_signal = \'ajax_submit\';\nif(!isset($check_referer)) $check_referer = \'yes\';\n\n$e = $modx->Event;\n\nif ($e->name == \'OnLoadWebDocument\' || $e->name == \'OnLoadWebPageCache\'){\n  \n  $output = \'\';\n  \n  $referer_valid = $check_referer==\'yes\' ? in_array(strpos($_SERVER[\'HTTP_REFERER\'],$_SERVER[\'HTTP_HOST\']),array(7,8)) : true;\n  \n  if(isset($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && strtolower($_SERVER[\'HTTP_X_REQUESTED_WITH\']) == \'xmlhttprequest\' && $referer_valid){\n    \n    if(isset($_POST[$post_signal])){\n        \n        class exDocumentParser extends DocumentParser {\n          function sendRedirect() {\n            return true;\n          }\n          function innerHTML($node){\n            $doc = new DOMDocument();\n            foreach ($node->childNodes as $child)\n                $doc->appendChild($doc->importNode($child, true));\n            return $doc->saveHTML();\n          }\n        }\n        \n        $xpath = $_POST[$post_signal];\n        \n        define(AS_PATH, MODX_BASE_PATH.\'assets/plugins/ajax_submit/\');\n        define(AS_URL_PATH, MODX_BASE_URL.\'assets/plugins/ajax_submit/\');\n        \n        $parser = new exDocumentParser;\n        $parser->db->connect();\n        $parser->getSettings();\n        $parser->config = $modx->config;\n        $parser->documentObject = $modx->documentObject;\n        $parser->documentIdentifier = $modx->documentIdentifier;\n        $parser->aliasListing = $modx->aliasListing;\n        $parser->snippetCache = $modx->snippetCache;\n        $parser->chunkCache = $modx->chunkCache;\n        $parser->documentListing = $modx->documentListing;\n        $parser->documentMap = $modx->documentMap;\n        \n        $html = $modx->documentContent;\n        $html = $parser->mergeChunkContent($html);\n        $html = $parser->mergeDocumentContent($html);\n        $html = $parser->mergeSettingsContent($html);\n        $html = $parser->parseDocumentSource($html);\n        if(strpos($html, \'[!\') > -1){\n            $html = str_replace(array(\'[!\',\'!]\'),array(\'[[\',\']]\'),$html);\n            $html = $parser->parseDocumentSource($html);\n        }\n        $html = $parser->rewriteUrls($html);\n        \n        if($modx->config[\'modx_charset\']==\"UTF-8\"){\n            $html = mb_convert_encoding($html, \'HTML-ENTITIES\', \'utf-8\');\n        }\n        \n        //search snippet content in html\n        require_once AS_PATH.\'Dom/Query.php\';\n        $dom = new Zend_Dom_Query($html);\n        $results = $dom->query($xpath);\n        if (count($results) > 0){\n            $output = $parser->innerHTML($results->current());\n            $output = trim($output);\n        }\n        \n        if(!$output) $output = \'success\';\n        \n        echo $output;\n        exit;\n        \n    }\n    \n  }\n  \n}\n','0','{\"post_signal\":[{\"label\":\"Post signal name\",\"type\":\"string\",\"value\":\"ajax_submit\",\"default\":\"ajax_submit\",\"desc\":\"\"}],\"check_referer\":[{\"label\":\"Check referer\",\"type\":\"list\",\"value\":\"yes\",\"options\":\"yes,no\",\"default\":\"yes\",\"desc\":\"\"}]}','0','','0','0');
INSERT INTO `epuz_site_plugins` VALUES ('16','SHKprintOrder','<strong>1.3.1</strong> Print ordering information','0','9','0','defined(\'IN_MANAGER_MODE\') or die();\n\n$print_icon = SHOPKEEPER_PATH.\'/style/default/img/print.png\'; //Иконка\nif(!isset($print_text)) $print_text = \'Печать\'; //Текст кнопки \nif(!isset($tpl_path)) $tpl_path = \'assets/plugins/SHKprintOrder/chunks/\'; //Папка с шаблонами\nif(!isset($print_tpl)) $print_tpl = \'@FILE:printOrder.tpl\'; //Шаблон (чанк)\n\n\n//Функция для перевода суммы прописью \nrequire_once MODX_BASE_PATH . \'assets/plugins/SHKprintOrder/num2str.php\';\nrequire_once MODX_BASE_PATH . \'assets/snippets/shopkeeper/classes/class.shopkeeper.php\';\nrequire_once MODX_BASE_PATH . \'assets/snippets/shopkeeper/classes/class.shk_manager.php\';\n\nif(!isset($data)) $data = array();\nif(!isset($purchases)) $purchases = array();\nif(!isset($addit_params)) $addit_params = array();\nif(!isset($p_allowed)) $p_allowed = array();  \n\n$e = &$modx->Event;\n\n$output = \"\";\n\nif ($e->name == \'OnSHKOrderDescRender\'){\n  \n  $shkm = new SHKmanager($modx);\n  $shkm->mod_table = $modx->db->config[\'table_prefix\'].\"manager_shopkeeper\";\n  $shkm->mod_config_table = $modx->db->config[\'table_prefix\'].\"manager_shopkeeper_config\";\n  $shkm->config[\'tplPath\'] = $tpl_path;\n  \n  //Кнопка печати\n  $output .= \'<li><a href=\"#print\" onclick=\"window.location.href=window.location.pathname+\\\'?a=112&id=\'.$_GET[\'id\'].\'&action=show_descr&item_id=\'.$_GET[\'item_id\'].\'&print=1\\\';return false;\"><img src=\"\'.$print_icon.\'\" alt=\"\">&nbsp; \'.$print_text.\'</a></li>\';\n  \n  //Выводим страницу для печати в новом окне\n  if(isset($_GET[\'item_id\']) && isset($_GET[\'print\']) && $_GET[\'print\']==1){\n  \n  $p_tpl_chunk = preg_split(\'/(\\[\\+loop\\+\\]|\\[\\+end_loop\\+\\])/s\', $shkm->fetchTpl($print_tpl));\n  $total_count = 0;\n  \n  //Убираем товары, которые нельзя заказать\n  foreach($purchases as $key => $value){\n    if(is_array($p_allowed) && !in_array($key,$p_allowed))\n      unset($purchases[$key]);\n    else\n      $total_count = $total_count + $value[1];\n  }\n  unset($key,$value);\n  \n  $shkm->config[\'currency\'] = $data[\'currency\'];\n  $shkm->config[\'orderDataTpl\'] = $print_tpl;\n  //Получаем список товаров по шаблону\n  $p_tpl_chunk[1] =  $shkm->getStuffList($purchases,$addit_params,\'list\');\n  \n  $print_tpl_out = implode(\'\',$p_tpl_chunk);\n  $print_tpl_out = addslashes($print_tpl_out);\n  \n  if($shkm->is_serialized($data[\"short_txt\"])){\n      $contact_fields = unserialize($data[\"short_txt\"]);\n      $contactsInfo = $shkm->renderContactInfo($contact_fields,$shkm->getConfig(\'conf_template\')); \n  }else{\n      $contactsInfo = $data[\"short_txt\"];\n  }\n  \n   //Парсим общий шаблон\n  $chunkArr = array(\n    \'totalPrice\' => $data[\'price\'],\n    \'totalPrice_propis\' => num2str($data[\'price\']),\n    \'currency\' => $data[\'currency\'],\n    \'date\' => $data[\'date\'],\n    \'today\' => date(\"d.m.Y\"),\n    \'contact\' => $contactsInfo,\n    \'site_name\' => $modx->config[\'site_name\'],\n    \'orderID\' => $data[\'id\'],\n    \'total_count\' => $total_count,\n    \'note\' => $data[\'note\'],\n    \'email\' => $data[\'email\'],\n    \'phone\' => $data[\'phone\']\n  );\n  foreach($chunkArr as $key => $val){\n    $print_tpl_out = str_replace(\"[+\".$key.\"+]\", $val, $print_tpl_out);\n  }\n  unset($key,$val);\n  \n  $print_tpl_out = preg_replace(\'/[\\r\\n]+/\',\'\',$print_tpl_out);\n  \n  //Выводим скрипт, который откроет окно с предпросмотром печати\n  $output .= <<< OUT\n      <script type=\"text/javascript\">\n        var newWindow = window.open(\'\', \'\', \'width=800,height=600,menubar=yes,location=no,toolbar=no\');\n        if (!newWindow) {\n          alert(\'Пожалуйста, разрешите всплывающие окна в браузере.\');\n        }\n        newWindow.document.write(\'$print_tpl_out\');\n        newWindow.focus();\n        newWindow.print();\n      </script>\nOUT;\n    \n  }\n  \n  $e->output($output);\n\n}\n\n','0','{\"print_text\":[{\"label\":\"Текст кнопки\",\"type\":\"string\",\"value\":\"Печать\",\"default\":\"Печать\",\"desc\":\"\"}],\"tpl_path\":[{\"label\":\"Папка с шаблонами\",\"type\":\"string\",\"value\":\"assets\\/plugins\\/SHKprintOrder\\/chunks\\/\",\"default\":\"assets\\/plugins\\/SHKprintOrder\\/chunks\\/\",\"desc\":\"\"}],\"print_tpl\":[{\"label\":\"Шаблон (чанк)\",\"type\":\"string\",\"value\":\"@FILE:printOrder.tpl\",\"default\":\"@FILE:printOrder.tpl\",\"desc\":\"\"}]}','0','','0','0');
